import React, { useRef, useEffect, useState } from 'react';

const Peoples = () => {
    const [scrollY, setScrollY] = useState(0);

    useEffect(() => {
        const handleScroll = () => {
            setScrollY(window.scrollY);
        };

        // Attach the scroll event listener
        window.addEventListener('scroll', handleScroll);

        // Clean up the event listener when the component unmounts
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    return (
        <div>
            <div
                style={{
                    position: 'fixed',
                    top: scrollY + 'px', // Update the position based on the scrollY state
                    left: '50%',
                    transform: 'translateX(-50%)',
                    backgroundColor: 'red',
                    padding: '10px',
                    color: 'white',
                }}
            >
                This element's position changes on scrolling. {scrollY}
            </div>
            <div style={{ marginTop: '100px' }}>
                Scroll position: {scrollY}px {/* Display the scroll position */}
            </div>
            <div style={{ height: '2000px' }}>
                {/* A tall div to make the page scrollable */}
            </div>
        </div>
    );
};
export default Peoples;
