import * as React from 'react';
import {Link} from "react-router-dom";
import mainPic from "../Images/mainPic.jpg";
import Folder from "../MenuIcons/FolderSimple.svg";
import Arrow from "../MenuIcons/CaretRight.svg";
import GridIcon from "../Images/View grid.svg";
import listIcon from "../Images/View list.svg";
import moreIcon from "../MenuIcons/ic_More.svg";
import './particularpage.css'
import {useEffect, useState} from "react";
import Alien from '../MenuIcons/Alien.svg';
import CarProfile from '../MenuIcons/CarProfile.svg';
import FilmReel from '../MenuIcons/FilmReel.svg';
import Planet from '../MenuIcons/Planet.svg';
import Rocket from '../MenuIcons/RocketLaunch.svg';
import Users from '../MenuIcons/Users.svg';
import view from '../DropDownMenuIcon/Frame.svg';





export function ParticularPage({dataFromChild,path,location}) {

    const [isGridSelectedLayout,setIsGridSelectedLayout]=useState(true);
    const [tabName,setTabName]=useState('');
    const [icon,setIcon]=useState(FilmReel);

    const [firstCol,setFirstCol]=useState('');
    const [secondCol,setSecondCol]=useState('');
    const [thirdCol,setThirdCol]=useState('');
    const fetchData = () => {

        if (location === '/films') {

            setTabName('Films');
            setIcon(FilmReel);
            setFirstCol('Name');
            setSecondCol('Director');
            setThirdCol('Release Date')
        } else if (location === '/planets') {
          setIcon(Planet);
            setTabName('Planets');
            setFirstCol('Name');
            setSecondCol('Terrain');
            setThirdCol('Population')
        }
        else if (location === '/people') {
            setIcon(Users);
            setTabName('People');
            setFirstCol('Name');
            setSecondCol('Gender');
            setThirdCol('Birth Year')
        }
        else if (location === '/starships') {
            setIcon(Rocket);
            setTabName('Starships');
            setFirstCol('Name');
            setSecondCol('Manufacturer');
            setThirdCol('Starship Class')
        }
        else if (location === '/species') {
            setIcon(Alien);
            setTabName('Species');
            setFirstCol('Name');
            setSecondCol('Classification');
            setThirdCol('Language')
        }
        else if (location === '/vehicles') {
            setIcon(CarProfile);
            setTabName('Vehicles');
            setFirstCol('Name');
            setSecondCol('model');
            setThirdCol('Vehicle Class')
        }
    };

    useEffect(() => {
        fetchData();
    }, [location]);

    const [isMoreClick,setIsMoreClick]=useState(false);
    const [selectedItem, setSelectedItem] = useState(null);
    const getMoreOption=(getname)=>{
        setIsMoreClick(!isMoreClick);
        setSelectedItem(getname);
        console.log(selectedItem);


    }





    return (
        <div className="">
            <div  className={`text-white  flex item-center relative cursor-pointer duration-300  rounded-md  mb-3 `}>

                <span className={`pt-1 text-gray-800`}><img src={icon}/></span>
                <span className={` font-medium ml-2  duration-300 flex-1 `}>{tabName}</span>
                <span className={`absolute flex item-center  duration-300  pt-1 ${isGridSelectedLayout?'selectedLayoutGrid':'normalLayoutGrid' }`} onClick={()=>setIsGridSelectedLayout(!isGridSelectedLayout)} ><img src={GridIcon} className={`layoutselectorsize`}/><span className={`text-black-50 my-0.5 ${isGridSelectedLayout?'block':'hidden'}`}>Grid</span></span>
                <span className={`absolute  flex item-center duration-300  pt-1  ${!isGridSelectedLayout?'selectedLayoutList':'normalLayoutList' }`} onClick={()=>setIsGridSelectedLayout(!isGridSelectedLayout)}><img src={listIcon} className={`layoutselectorsize`}/><span className={`text-black-50 my-0.5  ${!isGridSelectedLayout?'block':'hidden'}`}>List</span></span>
            </div>
            <div className="row duration-300">
                {/* Render a Card component for each movie */}

                {isGridSelectedLayout?  (
                        <>
                            {Object.keys(dataFromChild).map((title) => (


                                <div className="col-lg-4 col-md-6 mb-4">
                                    <div className="card duration-300 DataCard">
                                        <img src={mainPic} className="card-img-top dataImg " alt="..." width={10} height={10}/>
                                        <div className="card-body DataCardBody">
                                            <div  className={`text-white  flex item-center relative cursor-pointer duration-300  rounded-md  `}>

                                                <span className={`pt-1 text-gray-800`}><img src={icon}/></span>
                                                <span className={` font-medium ml-2  duration-300 flex-1 `}>{dataFromChild[title].first}</span>
                                                <span className={`absolute  duration-300 right-3 pt-1 dotIcon`} ><img src={moreIcon}/></span>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            ))}

                        </>

                    ):
                   <>
                       <div className={`row headList ml-0.5`}>
                           <div className="col-lg-5 col-md-6  p-3">
                               {firstCol}
                           </div>
                           <div className="col-lg-3 col-md-6  p-3 ">
                               {secondCol}
                           </div>
                           <div className="col-lg-3 col-md-6  p-3 ">
                               {thirdCol}
                           </div>
                           <div className="col-lg-1 col-md-6   p-3">

                           </div>
                       </div>
                   {Object.keys(dataFromChild).map((title) => (
                       <div className="col-lg-12 col-md-6 mb-2 mt-2">





                           <div className="row DataList" >

                               <div className="col-lg-5 col-md-6  p-3">
                                   <div  className={` text-white  flex item-center relative cursor-pointer duration-300  rounded-md  `}>
                                   <span className={`pt-1 text-gray-800`}><img src={icon}/></span>
                                   <span className={` font-medium ml-2  duration-300 flex-1 `}>{dataFromChild[title].first}</span>
                                   </div>
                               </div>
                               <div className="col-lg-3 col-md-6  p-3 text-white">
                                   {dataFromChild[title].second}
                               </div>
                               <div className="col-lg-3 col-md-6  p-3 text-white">
                                   {dataFromChild[title].third}

                               </div>
                               <div className="col-lg-1 col-md-6   p-3">
                                   <span className={`absolute  duration-300 right-3 pt-1 dotIcon`} onClick={() => getMoreOption(dataFromChild[title].first)} ><img src={moreIcon}/></span>
                               </div>


                           </div>
                           {isMoreClick && selectedItem === dataFromChild[title].first?  (
                                   <div>
                                       <div  className="text-white gap-x-4 flex items-center relative cursor-pointer duration-300 rounded-md mt-2 ">
                                           <span className="pt-1"><img src={view} alt="Drop Down Icon" /></span>
                                           <span className="text-white font-medium duration-300 flex-1">View</span>
                                       </div>
                                       <div>
                                           Download
                                       </div>
                                       <div>
                                           Rename
                                       </div>
                                       <div>
                                           Share Link
                                       </div>
                                       <div>
                                           Move
                                       </div>
                                       <div>
                                           Mark Private
                                       </div>
                                       <div>
                                           Delete
                                       </div>

                                   </div>

                               ):
                               <div></div>
                           }
                       </div>
                       ))}
                   </>
                }


            </div>
        </div>
    );
}
