import * as React from 'react';
import {Link} from "react-router-dom";
import './sidenav.css';
import Folder from '../MenuIcons/FolderSimple.svg';
import Arrow from '../MenuIcons/CaretRight.svg';
import {IoMdArrowBack} from "react-icons/io";
import {useState} from "react";

export function SideNav() {
    const [open,setOpen]=useState(true);
    const menuElements = [
        {
            icon:Folder,
            title: 'Films',
            path: '/films',
            arrow:Arrow,
        },
        {
            icon:Folder,
            title: 'People',
            path: '/people',
            arrow:Arrow,
        },
        {
            icon:Folder,
            title: 'Planets',
            path: '/planets',
            arrow:Arrow,
        }
        ,
        {
            icon:Folder,
            title: 'Species',
            path: '/species'
            ,
            arrow:Arrow,
        }
        ,
        {
            icon:Folder,
            title: 'Starships',
            path: '/starships',
            arrow:Arrow,

        }
        ,
        {
            icon:Folder,
            title: 'Vehicles',
            path: '/vehicles',
            arrow:Arrow,
        }

    ];
    return (
        <div className={``}>
            <div className={`bg-warning h-screen p-5 pt-8 ${open?'w-72':'w-20'} relative duration-300`}>
                <IoMdArrowBack className={`bg-white text-black-50 text-3xl rounded-full absolute -right-3 top-5 cursor-pointer ${!open && 'rotate-180'}`} onClick={()=>setOpen(!open)}/>
                <ul >
                    {menuElements.map((element, index) => (
                        <>
                            <li key={index} className={`text-white `}>
                                <span>{element.title}</span>
                            </li>
                        </>
                    ))}
                </ul>
                </div>


        </div>
    );
}
