import * as React from 'react';
import {BrowserRouter as Router, Route, Routes} from 'react-router-dom';
import './herosection.css';
import {SideNav} from "./SideNav";
import {DefaultPage} from "./DefaultPage";
import {ParticularPage} from "./ParticularPage";
import {useState} from "react";
import {CircleLoader} from "react-spinners";
import Peoples from "./Peoples";


export function HeroSection() {
    const [dataFromChild, setDataFromChild] = useState([]);
    const [path,setPath]=useState();
    const [location,setLocation]=useState('');
    const [spLoading ,setSpLoading]=useState(true);
    const handleDataFromChild = (data,path,loc,load) => {
        setDataFromChild(data);
        setPath(path);
        setLocation(loc);
        setSpLoading(false);
    };
    console.log(dataFromChild);

    return (
        <div>

            <div className="row sideBar">
                <div className="col-sm-12 col-md-3 col-lg-auto  sideNav ">
                    <SideNav sendDataToParent={handleDataFromChild}/>

                </div>
                <div className="col-sm-12 col-md-9 col-lg-9   HeroSectionMain ">



                    <Routes>
                        <Route path='/' element={<DefaultPage/>}/>
                        <Route path='/particularpage' element={
                            spLoading ?( <div className={`mt-72`}> <CircleLoader className={`m-auto `} color="#36d7b7" /> </div>): (<>
                            <ParticularPage dataFromChild={dataFromChild} path={path} location={location} load={spLoading}/>
                            </>)
                        } />
                    </Routes>








                </div>
            </div>


        </div>
    );
}
