import * as React from 'react';
import {Link} from "react-router-dom";
import './sidenav.css';
import Folder from '../MenuIcons/FolderSimple.svg';
import Arrow from '../MenuIcons/CaretRight.svg';
import {IoMdArrowBack} from "react-icons/io";
import {useEffect, useState} from "react";

export function SideNav() {
    const [open,setOpen]=useState(true);
    const [dropOpen,setDropOpen]=useState(false);
    const [menuSelectBg,setMenuSelectBg]=useState(false);

    const [selectedItem, setSelectedItem] = useState(null);

    const handleItemClick = (item) => {
        setSelectedItem(item);
    };

    const Dropfn=()=>{
        setDropOpen(!dropOpen);

    }
    useEffect(() => {
        fetchDataFromAPI();
    }, []);

    const fetchDataFromAPI = async () => {
        try {
            const response = await fetch('https://swapi.dev/api/');
            const data = await response.json();

        } catch (error) {
            console.error('Error fetching data: ', error);

        }
    };

    const menuElements = [
        {
            icon:Folder,
            title: 'Films',
            path: '/films',
            arrow:Arrow,
        },
        {
            icon:Folder,
            title: 'People',
            path: '/people',
            arrow:Arrow,
        },
        {
            icon:Folder,
            title: 'Planets',
            path: '/planets',
            arrow:Arrow,
        }
        ,
        {
            icon:Folder,
            title: 'Species',
            path: '/species'
            ,
            arrow:Arrow,
        }
        ,
        {
            icon:Folder,
            title: 'Starships',
            path: '/starships',
            arrow:Arrow,

        }
        ,
        {
            icon:Folder,
            title: 'Vehicles',
            path: '/vehicles',
            arrow:Arrow,
        }

    ];
    return (
        <div className={``}>
            <div className={` h-screen  ${open?'w-72':'w-24 -ms-6'} relative duration-300 sideNavbarMain`}>
                <div>
                <IoMdArrowBack className={`md:hidden sm:block bg-white text-black-50 text-2xl rounded-full absolute -right-3 top-5 cursor-pointer ${!open && 'rotate-180'}`} onClick={()=>setOpen(!open)}/>
                </div>
                <ul >
                    {menuElements.map((element, index) => (
                        <>
                            <li key={index} className={`text-white gap-x-4 flex item-center relative cursor-pointer hover:bg-white  duration-300  rounded-md mt-2 sideNavBar-${selectedItem && selectedItem.title === element.title ? 'selected' : 'unselected'} `} onClick={() => handleItemClick(element)}>
                                <span className={`pt-1 `}><img src={element.icon}/></span>
                                <span className={`  text-white font-medium  duration-300 flex-1 ${!open && 'hidden'}`}>{element.title}</span>
                                <span className={`${!open && 'hidden'} absolute  duration-300 right-3 pt-1 ${selectedItem && selectedItem.title === element.title? 'rotate-90' : 'rotate-0'}`} onClick={Dropfn}><img src={element.arrow}/></span>
                            </li>
                        </>
                    ))}
                </ul>
                </div>


        </div>
    );
}
