import * as React from 'react';
import {Link} from "react-router-dom";
import './herosection.css';
import {SideNav} from "./SideNav";
import Folder from "../MenuIcons/FolderSimple.svg";
import Arrow from "../MenuIcons/CaretRight.svg";

export function HeroSection() {
    const menuElements = [
        {
            icon:Folder,
            title: 'Films',
            path: '/films',
            arrow:Arrow,
        },
        {
            icon:Folder,
            title: 'People',
            path: '/people',
            arrow:Arrow,
        },
        {
            icon:Folder,
            title: 'Planets',
            path: '/planets',
            arrow:Arrow,
        }
        ,
        {
            icon:Folder,
            title: 'Species',
            path: '/species'
            ,
            arrow:Arrow,
        }
        ,
        {
            icon:Folder,
            title: 'Starships',
            path: '/starships',
            arrow:Arrow,

        }
        ,
        {
            icon:Folder,
            title: 'Vehicles',
            path: '/vehicles',
            arrow:Arrow,
        }

    ];

    return (
        <div>
            <div className="row sideBarMain">
                <div className="col-sm-12 col-md-2 col-lg-2 ">
                    <div>
                        <ul className={`sideMenuOptions`}>
                            {menuElements.map((element, index) => (
                                <li key={index} className={`sideMenuOption`}>

                                    <Link to={element.path} className={``}>

                                        <span className={`sideMenuOptionLink`}><img src={element.icon} alt={"menuLogo"} className={`sideNavFolderLogo`} />{element.title} <img src={element.arrow} alt={"arrowlogo"}  /></span>
                                    </Link>
                                </li>
                            ))}
                        </ul>



                    </div>
                </div>
                <div className="col-sm-12 col-md-10 col-lg-10 ">
                </div>
            </div>


        </div>
    );
}
