import * as React from 'react';
import {Link} from "react-router-dom";
import './defaultpage.css';
import mainPic from '../Images/mainPic.jpg';
import Folder from "../MenuIcons/FolderSimple.svg";
import Arrow from "../MenuIcons/CaretRight.svg";


export function DefaultPage() {

    const dropOpen=true;

    const menuElements = [
        {
            icon:Folder,
            title: 'Films',
            path: '/films',
            arrow:Arrow,
        },
        {
            icon:Folder,
            title: 'People',
            path: '/people',
            arrow:Arrow,
        },
        {
            icon:Folder,
            title: 'Planets',
            path: '/planets',
            arrow:Arrow,
        }
        ,
        {
            icon:Folder,
            title: 'Species',
            path: '/species'
            ,
            arrow:Arrow,
        }
        ,
        {
            icon:Folder,
            title: 'Starships',
            path: '/starships',
            arrow:Arrow,

        }
        ,
        {
            icon:Folder,
            title: 'Vehicles',
            path: '/vehicles',
            arrow:Arrow,
        }

    ];

    return (
        <div className={""}>
            {!dropOpen && (
            <div className="defaultPageBox">
                <div className="card mainCard" >
                    <img src={mainPic} className="card-img-top cardImage" alt="..."/>
                        <div className="card-body">
                            <h5 className="card-title ctitle">Welcome to Star Wars</h5>
                            <h5 className="card-title ctitle">Dashboard</h5>
                            <p className="cardQuotes">Star Wars is an American epic space opera multimedia franchise created by George Lucas, which began with the eponymous 1977 film and quickly became a worldwide pop culture phenomenon.</p>
                        </div>
                </div>



            </div>
            )}
            {dropOpen && (
                <div className="">
                    <h3 className="text-white font-bold">Movies:</h3>
                    <div className="row">
                        {/* Render a Card component for each movie */}
                        {menuElements.map((element, index) => (

                            <div className="col-lg-4 col-md-6 mb-4">
                                <div className="card">
                                    <img src={mainPic} className="card-img-top " alt="..." width={10} height={10}/>
                                    <div className="card-body DataCardBody">
                                        <div  className={`text-white  flex item-center relative cursor-pointer duration-300  rounded-md mt-2 `}>

                                            <span className={`pt-1 text-gray-800`}><img src={Folder}/></span>
                                            <span className={` font-medium  duration-300 flex-1 `}>{element.title}</span>
                                            <span className={`absolute  duration-300 right-3 pt-1 dotIcon`} ><img src={Arrow}/></span>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            )}


        </div>
    );
}
