import * as React from 'react';
import {Link} from "react-router-dom";
import './sidenav.css';
import Folder from '../MenuIcons/FolderSimple.svg';
import Arrow from '../MenuIcons/CaretRight.svg';
import {IoMdArrowBack} from "react-icons/io";
import {useState} from "react";

export function SideNav() {
    const [open,setOpen]=useState(true);
    const menuElements = [
        {
            icon:Folder,
            title: 'Films',
            path: '/films',
            arrow:Arrow,
        },
        {
            icon:Folder,
            title: 'People',
            path: '/people',
            arrow:Arrow,
        },
        {
            icon:Folder,
            title: 'Planets',
            path: '/planets',
            arrow:Arrow,
        }
        ,
        {
            icon:Folder,
            title: 'Species',
            path: '/species'
            ,
            arrow:Arrow,
        }
        ,
        {
            icon:Folder,
            title: 'Starships',
            path: '/starships',
            arrow:Arrow,

        }
        ,
        {
            icon:Folder,
            title: 'Vehicles',
            path: '/vehicles',
            arrow:Arrow,
        }

    ];
    return (
        <div className={``}>
            <div className={`bg-warning h-screen  ${open?'w-72':'w-20'} relative duration-300 sideNavbarMain`}>
                <div>
                <IoMdArrowBack className={`md:hidden sm:block bg-white text-black-50 text-3xl rounded-full absolute -right-3 top-5 cursor-pointer ${!open && 'rotate-180'}`} onClick={()=>setOpen(!open)}/>
                </div>
                <ul >
                    {menuElements.map((element, index) => (
                        <>
                            <li key={index} className={`text-white gap-x-4 flex item-center relative cursor-pointer hover:bg-white  rounded-md mt-2 sideNavbarElement`}>
                                <span className={` `}><img src={element.icon}/></span>
                                <span className={`text-black-50 font-medium flex-1 ${!open && 'hidden'}`}>{element.title}</span>
                                <span className={`absolute -right-12`}><img src={element.arrow}/></span>
                            </li>
                        </>
                    ))}
                </ul>
                </div>


        </div>
    );
}
