import * as React from 'react';
import {BrowserRouter as Router, Route, Routes} from 'react-router-dom';
import './herosection.css';
import {SideNav} from "./SideNav";
import {DefaultPage} from "./DefaultPage";
import {ParticularPage} from "./ParticularPage";
import {useEffect, useState} from "react";
import {CircleLoader} from "react-spinners";
import Peoples from "./Peoples";
import {DeleteComponent} from "./DeleteComponent";
import {TopBar} from "./TopBar";


export function HeroSection() {
    const [dataFromChild, setDataFromChild] = useState([]);
    const [path,setPath]=useState();
    const [location,setLocation]=useState('');
    const [spLoading ,setSpLoading]=useState(true);
    const handleDataFromChild = (data,path,loc,load) => {
        setDataFromChild(data);
        setPath(path);
        setLocation(loc);
        setSpLoading(false);
    };
    console.log(dataFromChild);

    const [inputValue, setInputValue] = useState('');
 const handleInputChange = (value) => {
        setInputValue(value);
    };
 const [menuShow,setMenuShow]=useState(false);
    const [screenWidth, setScreenWidth] = useState(window.innerWidth);

    // Update the state whenever the screen size changes
    const handleResize = () => {
        setScreenWidth(window.innerWidth);
    };

    useEffect(() => {
        // Add an event listener for the "resize" event to update the screen width on window resize
        window.addEventListener('resize', handleResize);

        // Clean up the event listener when the component unmounts
        return () => {
            window.removeEventListener('resize', handleResize);
        };
    }, []);

    return (
        <div>
            <TopBar onChangeInput={handleInputChange}/>


            <div className="row sideBar">

                {screenWidth<768 ?( <>

                    <div className=" col-md-3 col-lg-3  sideNav ">
                        <button className={`MenuButton`}>Menu</button>
                    <SideNav sendDataToParent={handleDataFromChild}/>

                </div>
                    </>):
                    ( <div className=" col-md-3 col-lg-3  sideNav ">
                        <SideNav sendDataToParent={handleDataFromChild}/>

                    </div>)}


                <div className="col-sm-12 col-md-9 col-lg-9   HeroSectionMain ">



                    <Routes>
                        <Route path='/' element={<DefaultPage/>}/>
                        <Route path='/particularpage' element={
                            spLoading ?( <div className={`mt-72`}> <CircleLoader className={`m-auto `} color="#CB1A80" size={120} /> </div>): (<>
                            <ParticularPage dataFromChild={dataFromChild} path={path} location={location} load={spLoading} inputValue={inputValue}/>
                            </>)
                        } />
                    </Routes>
                    








                </div>
            </div>


        </div>
    );
}
