import * as React from 'react';
import {Link} from "react-router-dom";
import './sidenav.css';
import Folder from '../MenuIcons/FolderSimple.svg';
import Arrow from '../MenuIcons/CaretRight.svg';
import {IoMdArrowBack} from "react-icons/io";
import {useEffect, useState} from "react";

export function SideNav() {
    const [open,setOpen]=useState(true);
    const [isOpen, setIsOpen] = useState(false);
    const [data, setData] = useState([]);

    const toggleDropdown = () => {
        setIsOpen(!isOpen);
    };

    useEffect(() => {
        // Fetch data from the API and store it in the data state variable
        fetchDataFromApi();
    }, []);

    const fetchDataFromApi = () => {
        // Make API call using fetch() or any other library like axios
        fetch('https://swapi.dev/api/')
            .then((response) => response.json())
            .then((data) => setData(data))
            .catch((error) => console.error('Error fetching data:', error));
    };
    const menuElements = [
        {
            icon:Folder,
            title: 'Films',
            path: '/films',
            arrow:Arrow,
        },
        {
            icon:Folder,
            title: 'People',
            path: '/people',
            arrow:Arrow,
        },
        {
            icon:Folder,
            title: 'Planets',
            path: '/planets',
            arrow:Arrow,
        }
        ,
        {
            icon:Folder,
            title: 'Species',
            path: '/species'
            ,
            arrow:Arrow,
        }
        ,
        {
            icon:Folder,
            title: 'Starships',
            path: '/starships',
            arrow:Arrow,

        }
        ,
        {
            icon:Folder,
            title: 'Vehicles',
            path: '/vehicles',
            arrow:Arrow,
        }

    ];
    return (
        <div className={``}>
            <div className={`bg-warning h-screen p-5 pt-8 ${open?'w-72':'w-20'} relative duration-300`}>
                <IoMdArrowBack className={`bg-white text-black-50 text-3xl rounded-full absolute -right-3 top-5 cursor-pointer ${!open && 'rotate-180'}`} onClick={()=>setOpen(!open)}/>
                <ul >
                    {menuElements.map((element, index) => (
                        <>
                            <li key={index} className={`text-white gap-x-4 flex item-center relative cursor-pointer hover:bg-white  rounded-md mt-2 `}>
                                <span className={` `}><img src={element.icon}/></span>
                                <span className={`text-black-50 font-medium flex-1 ${!open && 'hidden'}`}>{element.title}</span>
                                <span className={`absolute -right-0`}><img src={element.arrow} onClick={toggleDropdown}/></span>
                                {isOpen && (
                                    <div className="dropdown-options">
                                        {data.map((item) => (
                                            <div className="dropdown-option">
                                                {item.title}
                                            </div>
                                        ))}
                                    </div>
                                )}
                            </li>
                        </>
                    ))}
                </ul>
                </div>


        </div>
    );
}
