import * as React from 'react';
import './details.css';
import cross from "../MenuIcons/ic_Close.svg";
import img from "../Images/mainPic.jpg"


export function Details() {


    return (
        <div className={"SelectedBox"}>
            <div className={`topBar text-white gap-x-4 flex item-center relative cursor-pointer   duration-300   mt-2  `}>

                <span className={`  text-white font-medium  duration-300 flex-1 `}>Movie Details</span>
                <span className={` absolute  duration-300 right-3 pt-1  hover:rotate-90 right-0`} ><img src={cross}/></span>
            </div>
            <img src={img} alt={"Selected Img"} className={"SelectedIng"}/>
           <div className={`text-white mt-3`}>
               <h2 className={'textLabel'}>Title</h2>
               <h2 className={`textBox`} >movies Name</h2>
               <h2 className={'textLabel'}>Opening Crawl</h2>
               <h2 className={`textBox`}  >movies Name</h2>
               <h2 className={'textLabel'}>Genere</h2>
               <h2 className={`textBox`} >movies Name</h2>
           </div>
            <div className={`bottomBar text-white gap-x-4 flex item-center relative cursor-pointer   duration-300   mt-2  `}>

                <span className={`  text-white font-medium  duration-300 flex-1 `}>Close</span>

            </div>




        </div>
    );
}
